n p.sky_branch_id = sky.sky_branch_id left join machine_room_degree mr on p.machine_room_id = mr.machine_room_id left join electric_power_degree ep on p.electric_power_id = ep.electric_power_id left join main_equipment_degree me on p.main_equipment_id = me.main_equipment_id left join Air_conditioner_degree ac on p.air_conditioner_id = ac.air_conditioner_id left join fire_fighting_device_degree ff on p.fire_id = ff.fire_id left join power_monitoring_degree pm on p.power_monitoring_id = pm.power_monitoring_id
and PROVINCE like #{province}
and CITY like #{city}
and COUNTY like #{county}
and BUILD_YEAR like #{buildYear}
and BUILDING_PROJECT like #{buildingProject}
and OPERATOR like #{operator}
and (ON_SITE_SUPERVISION like #{keyword} or PROFESSION_PEOPLE like #{keyword} or DISTRICT_HEAD like #{keyword})
)t )where r between #{start} and #{end}
